mapper.update:
update tj_case set status=#{status}
where id in

    #{id}

mapper.select:
select c.id, c.test_type, c.case_number, c.hazard_index, c.label, c.fragmented_scene_id, c.resources_detail_id, c.test_target, c.eva_object, c.status, c.created_by, c.created_date, c.updated_date, rd.name as resourcesName from tj_case c left join tj_resources_detail rd on c.resources_detail_id = rd.id
and c.test_type = #{testType} and c.status = #{status} and c.fragmented_scene_id = #{fragmentedSceneId}
and c.case_number like concat('%',#{name},'%')
and c.label like concat('%',#{label},'%')
and c.resources_detail_id = #{resourcesDetailId}
and c.hazard_index = #{hazardIndex}

select count(*)
from tj_case
where fragmented_scene_id = #{sceneId}

select s.*
from tj_fragmented_scenes s

    and s.id in (
        select distinct fragmented_scene_id
        from tj_case
        where test_type=#{testType}
    )
    and s.type=#{type}
