mapper.update:
update tj_case set status=#{status}
where id in

    #{id}

mapper.select:
select c.id, c.test_type, c.case_number, sd.hazardIndex, c.label, c.scene_detail_id, c.resources_detail_id, c.test_target, c.eva_object, c.status, c.created_by, c.created_date, c.updated_date, rd.name as resourcesName from tj_case c inner join tj_fragmented_scene_detail sd on sd.id = c.scene_detail_id left join tj_resources_detail rd on rd.id = c.resources_detail_id
and c.test_type = #{testType} and c.status = #{status} and c.scene_detail_id = #{sceneDetailId}
and c.case_number like concat('%',#{caseNumber},'%')
and c.label like concat('%',#{label},'%')
and c.resources_detail_id = #{resourcesDetailId}

select count(*)
from tj_case
where fragmented_scene_id = #{sceneId}
