mapper.select:
select a.id, a.task_id , a.case_id, a.sort, a.create_time, a.status, a.passing_rate, a.start_time, a.end_time, a.test_total_time , a.test_total_time, a.connect_info, b.case_number, b.tree_id from tj_task_case a left join tj_case b on a.case_id = b.id
and a.task_id = #{taskId}
and a.case_id = #{caseId}

SELECT ttc.id, ttc.task_id, ttc.case_id, ttc.sort, ttc.`status`, a.case_number, a.scene_name, a.all_stage_label, a.case_detail_info, a.case_route_file, a.real_detail_info, a.real_route_file, tcr.id as record_id, tcr.detail_info, tcr.route_file, tcr.status as record_status FROM tj_task_case ttc INNER JOIN ( SELECT tc.id, tc.case_number, tc.route_file as case_route_file, tc.detail_info as case_detail_info, sd.`name` AS scene_name, sd.all_stage_label, rr.detail_info AS real_detail_info, rr.route_file AS real_route_file FROM tj_case tc INNER JOIN ( SELECT fsd.id, fs.NAME, fsd.all_stage_label FROM tj_fragmented_scene_detail fsd LEFT JOIN tj_fragmented_scenes fs ON fsd.fragmented_scene_id = fs.id ) sd ON sd.id = tc.scene_detail_id LEFT JOIN tj_case_real_record rr ON rr.case_id = tc.id ) a ON a.id = ttc.case_id LEFT JOIN tj_task_case_record tcr ON (tcr.task_id = ttc.task_id AND tcr.case_id = ttc.case_id)
and ttc.task_id = #{taskId}
and ttc.case_id = #{caseId}
and ttc.id = #{id}
ORDER BY ttc.sort
