mapper.select:
select
    tt.`status`
from tj_task tt
LEFT JOIN (
    SELECT
    dc.task_id,
    dd.device_id,
    dd.device_type as object_type,
    dd.device_name as brand,
    dd.ip as plate_number,
    dd.service_address as sign
    FROM tj_task_data_config dc
    LEFT JOIN tj_device_detail dd ON dd.device_id = dc.device_id
    WHERE dc.type = 'av'
) a ON a.task_id = tt.id


        AND tt.task_code LIKE CONCAT('%', #{taskCode}, '%')


        AND a.plate_number LIKE CONCAT('%', #{plateNumber}, '%')


        AND a.sign LIKE CONCAT('%', #{sign}, '%')


        AND tt.client LIKE CONCAT('%', #{client}, '%')


        AND tt.create_time >= #{startCreateTime}


        AND tt.create_time <= #{endCreateTime}


        AND tt.plan_date >= #{startPlanDate}


        AND tt.plan_date <= #{endPlanDate}


        AND tt.test_type = #{testType}


        AND a.object_type = #{objectType}


        AND tt.status = #{status}

SELECT tt.id, tt.client, tt.consigner, tt.contract, tt.task_code, tt.plan_date, tt.create_time, tt.test_type, tt.case_count, a.device_id, a.object_type, a.brand, a.plate_number, a.sign, tt.test_total_time, tt.status, tt.route_file FROM tj_task tt LEFT JOIN ( SELECT dc.task_id, dd.device_id, dd.device_type as object_type, dd.device_name as brand, dd.ip as plate_number, dd.service_address as sign FROM tj_task_data_config dc LEFT JOIN tj_device_detail dd ON dd.device_id = dc.device_id WHERE dc.type = 'av' ) a ON a.task_id = tt.id
AND tt.task_code LIKE CONCAT('%', #{taskCode}, '%')
AND a.plate_number LIKE CONCAT('%', #{plateNumber}, '%')
AND a.sign LIKE CONCAT('%', #{sign}, '%')
AND tt.client LIKE CONCAT('%', #{client}, '%')
AND tt.create_time >= #{startCreateTime}
AND tt.create_time <= #{endCreateTime}
AND tt.plan_date >= #{startPlanDate}
AND tt.plan_date <= #{endPlanDate}
AND tt.test_type = #{testType}
AND a.object_type = #{objectType}
AND tt.status = #{status}
AND tt.id = #{id}
order by tt.plan_date desc, tt.create_time desc
